// components/Dashboard.jsx
import React, { useState, useEffect } from 'react';
import {
  Box,
  Grid,
  Paper,
  Typography,
  Card,
  CardContent,
  CircularProgress,
  Alert,
} from '@mui/material';
import {
  Phone,
  CheckCircle,
  Cancel,
  TrendingUp,
  AccessTime,
  People,
} from '@mui/icons-material';
import { dashboardAPI } from '../services/api';
import { format } from 'date-fns';
import { es } from 'date-fns/locale';

// Componente de tarjeta de estadística
const StatCard = ({ title, value, subtitle, icon: Icon, color, trend }) => (
  <Card sx={{ height: '100%', position: 'relative', overflow: 'visible' }}>
    <CardContent>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
        <Box>
          <Typography color="text.secondary" gutterBottom variant="overline">
            {title}
          </Typography>
          <Typography variant="h4" component="div" fontWeight="bold">
            {value}
          </Typography>
          {subtitle && (
            <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
              {subtitle}
            </Typography>
          )}
          {trend && (
            <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
              <TrendingUp 
                sx={{ 
                  fontSize: 16, 
                  mr: 0.5, 
                  color: trend > 0 ? 'success.main' : 'error.main' 
                }} 
              />
              <Typography 
                variant="body2" 
                color={trend > 0 ? 'success.main' : 'error.main'}
              >
                {trend > 0 ? '+' : ''}{trend}% vs ayer
              </Typography>
            </Box>
          )}
        </Box>
        <Box
          sx={{
            bgcolor: `${color}.main`,
            borderRadius: 2,
            p: 1.5,
            color: 'white',
          }}
        >
          <Icon sx={{ fontSize: 32 }} />
        </Box>
      </Box>
    </CardContent>
  </Card>
);

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [data, setData] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(new Date());

  const fetchData = async () => {
    try {
      setError(null);
      const response = await dashboardAPI.getSummary();
      setData(response.data.data);
      setLastUpdate(new Date());
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();

    // Actualizar cada 30 segundos
    const interval = setInterval(fetchData, 30000);

    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '60vh' }}>
        <CircularProgress size={60} />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert severity="error" sx={{ m: 2 }}>
        Error al cargar el dashboard: {error}
      </Alert>
    );
  }

  const { today, yesterday, week, queues, agents } = data;

  // Calcular tendencias
  const calculateTrend = (current, previous) => {
    if (!previous || previous === 0) return 0;
    return Math.round(((current - previous) / previous) * 100);
  };

  const callsTrend = calculateTrend(
    today?.total_calls || 0,
    yesterday?.total_calls || 0
  );

  const answeredTrend = calculateTrend(
    today?.answered_calls || 0,
    yesterday?.answered_calls || 0
  );

  // Contar agentes activos
  const activeAgents = agents?.filter(a => a.status !== 'UNAVAILABLE').length || 0;

  return (
    <Box sx={{ flexGrow: 1, p: 3 }}>
      {/* Header */}
      <Box sx={{ mb: 3 }}>
        <Typography variant="h4" fontWeight="bold" gutterBottom>
          Dashboard General
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Última actualización: {format(lastUpdate, "dd 'de' MMMM, HH:mm:ss", { locale: es })}
        </Typography>
      </Box>

      {/* Estadísticas principales */}
      <Grid container spacing={3} sx={{ mb: 3 }}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Llamadas Hoy"
            value={today?.total_calls || 0}
            subtitle={`${week?.total_calls || 0} esta semana`}
            icon={Phone}
            color="primary"
            trend={callsTrend}
          />
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Contestadas"
            value={today?.answered_calls || 0}
            subtitle={`${today?.answer_rate || 0}% tasa de respuesta`}
            icon={CheckCircle}
            color="success"
            trend={answeredTrend}
          />
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Abandonadas"
            value={today?.abandoned_calls || 0}
            subtitle={`${Math.round((today?.abandoned_calls || 0) / (today?.total_calls || 1) * 100)}% del total`}
            icon={Cancel}
            color="error"
          />
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Agentes Activos"
            value={activeAgents}
            subtitle={`${agents?.length || 0} en total`}
            icon={People}
            color="info"
          />
        </Grid>
      </Grid>

      {/* Métricas adicionales */}
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" fontWeight="bold" gutterBottom>
              Tiempo Promedio
            </Typography>
            <Box sx={{ mt: 2 }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
                <Typography variant="body2">Duración de llamada:</Typography>
                <Typography variant="body2" fontWeight="bold">
                  {Math.round(today?.avg_duration || 0)} seg
                </Typography>
              </Box>
              <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                <Typography variant="body2">Tiempo de conversación:</Typography>
                <Typography variant="body2" fontWeight="bold">
                  {Math.round(today?.avg_billsec || 0)} seg
                </Typography>
              </Box>
            </Box>
          </Paper>
        </Grid>

        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" fontWeight="bold" gutterBottom>
              Estado de Colas
            </Typography>
            <Box sx={{ mt: 2 }}>
              {queues && queues.length > 0 ? (
                queues.map((queue, index) => (
                  <Box 
                    key={index} 
                    sx={{ 
                      display: 'flex', 
                      justifyContent: 'space-between', 
                      mb: 2,
                      pb: 1,
                      borderBottom: index < queues.length - 1 ? '1px solid #eee' : 'none'
                    }}
                  >
                    <Typography variant="body2">{queue.queue_name}</Typography>
                    <Typography variant="body2" fontWeight="bold">
                      {queue.calls_completed_5min || 0} llamadas (5 min)
                    </Typography>
                  </Box>
                ))
              ) : (
                <Typography variant="body2" color="text.secondary">
                  No hay datos disponibles
                </Typography>
              )}
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
